-- ----------------------------
-- Procedure structure for vcBlogCategory
-- ----------------------------
DROP PROCEDURE IF EXISTS `vcBlogCategory`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `vcBlogCategory`(IN `ctID` INT,	IN `limit` VARCHAR (20),IN `orderType` VARCHAR (50),IN `type` CHAR (1))
BEGIN
	DECLARE
		lev INT ; /*########参数说明##########
	ctID：分类id int, 
	limit 字符串如'0,10',
	orderType：按哪个字段排序，
	status：审核
	type=1文章
	type=2视频
*/
		#层深
	SET lev = 1 ; TRUNCATE TABLE tmp1 ; #清空临时表
	#插入第一层数据
	INSERT tmp1 SELECT
		id,
		p_id,
		1
	FROM
		ts_square_category
	WHERE
		id = ctID ; #通过连接递归
	WHILE row_count() > 0 DO

	SET lev = lev + 1 ; INSERT tmp1 SELECT
		t.id,
		t.p_id,
		lev
	FROM
		ts_square_category t
	JOIN tmp1 a ON t.p_id = a.ID
	AND levv = lev - 1 ;
	END
	WHILE ; ##############文章##################
	IF type = 1 THEN

	SET @tsql = CONCAT(
		'SELECT
										b.id,b.uid author_id,b.title,b.content,u.uname author_name,b.readCount,b.commentCount,b.cTime,b.private
										FROM
										ts_blog b
										INNER JOIN tmp1 on tmp1.id=b.square_id
										INNER JOIN ts_user u on u.uid=b.uid
										WHERE b.private=0 and b.status=1
										ORDER BY ',
		orderType,
		' desc
										LIMIT ',
		`limit`,
		'
										;'
	) ; PREPARE stmt1
	FROM
		@tsql ; EXECUTE stmt1 ; ###############视频##################
	ELSEIF type = 2 THEN

	SET @tsql = CONCAT(
		'SELECT
										v.id,v.userId author_id,u.uname author_name,v.name title,v.info,v.commentCount,v.readCount,v.serverpath,v.savepath,v.outfilename,v.fcode,v.cTime
										FROM
										ts_video v
										INNER JOIN ts_square_video s on v.id=s.video_id
										INNER JOIN tmp1 on tmp1.id=s.category_id
										INNER JOIN ts_user u on u.uid=v.userId
										WHERE isDel=0
										ORDER BY ',
		orderType,
		' desc
										LIMIT ',
		`limit`,
		'
										;'
	) ; PREPARE stmt1
	FROM
		@tsql ; EXECUTE stmt1 ;

###############################资源###################################################
	ELSEIF type = 3 THEN

	SET @tsql = CONCAT(
		'SELECT
				r.id,r.uid author_id,u.uname author_name,r.title,r.info,r.readCount,r.downCount,f.saveaddress,r.downCount,f.savepath,f.savename,f.savetype,r.time cTime
		 FROM
				ts_resource r
				INNER JOIN tmp1 on tmp1.id=r.section
				INNER JOIN ts_resource_file f on f.id=r.id
				INNER JOIN ts_user u on u.uid=r.uid
		 ORDER BY ',
				 orderType,' desc
		LIMIT ',
					`limit`,'
		;'
	) ; PREPARE stmt1
	FROM
		@tsql ; EXECUTE stmt1 ;

	END
	IF ;
	END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for vcCategory
-- ----------------------------
DROP PROCEDURE IF EXISTS `vcCategory`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `vcCategory`(IN idd VARCHAR(36))
BEGIN
	DECLARE	lev INT;


SET lev = 1;

DROP TABLE IF EXISTS tmp3;

CREATE TABLE tmp3 (
	id VARCHAR (36),
	category_name VARCHAR (50),
	p_id VARCHAR (36),
	levv INT
);

INSERT tmp3 SELECT
	id,
	category_name,
	p_id,
	1
FROM
	`ts_square_category`
WHERE
	p_id = idd;


WHILE row_count() > 0 DO

SET lev = lev + 1;

INSERT tmp3 SELECT
	t.id,
	t.`category_name`,
	t.`p_id`,
	lev
FROM
	ts_square_category t
JOIN tmp3 a ON t.p_id = a.ID
AND levv = lev - 1;


END
WHILE;

INSERT tmp3 SELECT
	id,
	category_name,
	p_id,
	0
FROM
	`ts_square_category`
WHERE
	id = idd;

SELECT
	*
FROM
	tmp3;


END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for vcPath
-- ----------------------------
DROP PROCEDURE IF EXISTS `vcPath`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `vcPath`(IN `cID` INT)
BEGIN
	DECLARE
		lev INT;

#层深
SET lev = 1;

TRUNCATE TABLE tmp2;

#插入第一层数据
INSERT tmp2 SELECT
	ID,
	p_id,
	1,
	category_name
FROM
	ts_square_category
WHERE
	ID = cID;

#通过连接递归
WHILE row_count() > 0 DO

SET lev = lev + 1;

INSERT tmp2 SELECT
	t.ID,
	t.p_id,
	lev,
	t.category_name
FROM
	ts_square_category t
JOIN tmp2 a ON t.id = a.ParentID
AND levv = lev - 1;


END
WHILE;

SELECT
	tmp2.ID,
	tmp2.cName
FROM
	tmp2
ORDER BY
	tmp2.levv DESC;


END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for vcTree
-- ----------------------------
DROP PROCEDURE IF EXISTS `vcTree`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `vcTree`(IN `ctID` INT,IN `lmtCnt` INT,IN `orderType` VARCHAR (50))
BEGIN
	DECLARE
		lev INT;

#层深
SET lev = 1;

TRUNCATE TABLE tmp1;

#清空临时表
#插入第一层数据
INSERT tmp1 SELECT
	ID,
	p_id,
	1
FROM
	ts_square_category
WHERE
	p_id = ctID;

#通过连接递归
WHILE row_count() > 0 DO

SET lev = lev + 1;

INSERT tmp1 SELECT
	t.ID,
	t.p_id,
	lev
FROM
	ts_square_category t
JOIN tmp1 a ON t.p_id = a.ID
AND levv = lev - 1;


END
WHILE;

#插入本层数据
INSERT tmp1 SELECT
	ID,
	p_id,
	1
FROM
	ts_square_category
WHERE
	ID = ctID;

#执行动态条数查询
SET @tsql = CONCAT(
	'SELECT
				ts_video.id,
				ts_video.userId,
				ts_video.`name`,
				ts_video.cTime,
				ts_video.readCount,
				ts_video.commentCount,
				ts_video.flvsize,
				ts_video.times,
				ts_video.categoryId as cid,
				ts_video_category.`name` as catename
				FROM
				ts_video
				INNER JOIN ts_square_video ON ts_video.id = ts_square_video.video_id
				INNER JOIN tmp1 ON ts_square_video.category_id = tmp1.ID
				LEFT JOIN  ts_video_category ON ts_video.categoryId = ts_video_category.id
				WHERE ts_video.status = 1
				ORDER BY ',
	orderType,
	' desc
										LIMIT 0,',
	lmtCnt,
	'
										;'
);

PREPARE stmt1
FROM
	@tsql;

EXECUTE stmt1;


END
;;
DELIMITER ;
